import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
const Portal = () => {

    const [alunoInfo, setAluoinfo] = useState([])
    useEffect(() => {
        const id = localStorage.getItem('IdAluno')
        axios.get(`http://localhost:3001/getAlunoInfo/${id}`).then((response) => {
            if (response.data) {
                setAluoinfo(response.data)
            } else {
                setAluoinfo('Erro')
            }
        })
    })
    return (

        <>  <center>
            <h2>Olá {alunoInfo.nome}</h2>
            <p>Sua nota em Portugues é {alunoInfo.portugues}</p>
            <p>Sua nota em Matematica é {alunoInfo.matematica}</p>
            <p>Sua nota em Historia é {alunoInfo.historia}</p>
            <p>Sua nota em Geografia é {alunoInfo.geografia}</p>
        </center >
        </>



    )

}
export default Portal
