import axios from "axios";
import React, { useEffect, useState } from "react";
import './style.css'
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import '../components/professor.css'
import xsvg from '../images/xsvg.svg'
const AdmProf = () => {
    const navigate = useNavigate()
    const [responsaAlunos, setResponseAlunos] = useState([])
    const [responseProfessor, setResponseProfessor] = useState([])
    const [Notas, setNota] = useState('')
    const [alunoBuscado, setAlunoB] = useState('')
    const [materia, setMateria] = useState('')
    const [unidade, setUnidade] = useState('U1')
    const [lupaAluno, setLupaAluno] = useState('')
    const [notasResponse, setNotasResponse] = useState([])
    const [alunoclicadom, setalunoclicado] = useState('')
    const [id, setId] = useState('')
    useEffect(() => {
        axios.get('http://localhost:3001/getInfoAlunos').then((response) => {
            setResponseAlunos(response.data)
        })
        const id = localStorage.getItem('ProfessorID')
        axios.get(`http://localhost:3001/getInfoProfessor/${id}`).then((response) => {
            setResponseProfessor(response.data)
        })
        aa()
    }, [aa])
    function aa() {
        setMateria(String(responseProfessor[1]))
    }
    function verNotasAluno(e) {
        e.preventDefault()
        setMateria(String(responseProfessor[1]))
        alert(materia)

        axios.post(`http://localhost:3001/getNotasAluno/${materia}`, { lupaAluno }).then((response) => {
            if (response.data === 'Erro') {
                alert('Aluno não encontrado')
            } else {
                setNotasResponse(JSON.stringify(response.data))
            }
        })
    }
    function Adcnota(e) {
        setMateria(String(responseProfessor[1]))
        e.preventDefault()
        if (Notas > 10) {
            alert('Insira um numero de 0 a 10')
            return
        }
        axios.put(`http://localhost:3001/addNotas/${materia}`, { Notas, unidade }).then((response) => {
            if (response.data === 'ERRO') {
                alert('Aluno não encontrado')
            } else {
                alert('Nota adicionada com sucesso')
            }
        })
    }
    function logout() {
        localStorage.removeItem('ProfessorID')
        localStorage.removeItem('tokenadminon')
        navigate('/')
    }

    function darNotas() {

    }
    function buscarNotas() {

    }
    function fechar(){
        const blackscreen = document.querySelector('.blackscreenopacity')
        blackscreen.classList.toggle('block')
    }
    function edtnota(e) {
        const id = e.currentTarget.id
        const blackscreen = document.querySelector('.blackscreenopacity')
       setId(id)
        blackscreen.classList.toggle('block')
        axios.get(`http://localhost:3001/getNomeAluno/${id}`).then((response) => {
            setalunoclicado(String(response.data))
        })
    }
    return (
        <>
            <header className="headerlogin headlc">
                <div className="headerloginc">
                    <span className="logo-span-header"><Link to={'/'}>SIGEN</Link></span>
                </div>
            </header>

            <div className="maincontainerprof">

                <div className="flexmainprof">
                    <div className="fp1">
                        <div className="divalunosp">
                            <h1>Olá Professor <span className="specialspan">{responseProfessor[0]}</span> </h1> <br />
                            <h3>Sua materia é: <span className="specialspan">{responseProfessor[1]}</span> </h3>

                            <h3>Abaixo, estara uma lista com todos os alunos do terceiro ano de informatica matriculados no SIGEN.</h3>
                            <div className="containerdivlist-a ">
                                {responsaAlunos.map((e) => {
                                    return (
                                        <div className="list-div-a">
                                            <p key={e.id}>UID: {e.id} | Nome: {e.nome} | Telefone: {e.telefone} | Sexo: {e.sexo} | Turma: {e.turma} </p>
                                            <button className="editarnota" id={e.id} onClick={edtnota} >Editar Nota</button>
                                        </div>

                                    )
                                })}
                            </div>
                            <span className="absolutespanqtd">Qtd de Alunos: <span className="specialspan">{responsaAlunos.length}</span></span>
                            <div className="buttongroup">
                                <div className="divbtng2">
                                    <button className="submitbutton submitbuttonPf btn22" onClick={buscarNotas} >Buscar Notas</button>

                                </div>

                            </div>
                        </div>

                    </div>
                    {/* <div className="fp2">
                    <h2>Digite o nome do aluno que você quer editar as notas</h2><br />
                    <form onSubmit={Adcnota}>
                        <span>Nome:</span> <input type={'text'} onChange={(e) => { setAlunoB(e.target.value) }} required /> <br />
                        <span>Nota:</span>
                        <input step="0.01" type={'number'} required onChange={(e) => { setNota(e.target.value) }} /> <br />
                        <select name="unidades" onChange={(e) => { setUnidade(e.target.value) }} required>
                            <option value="U1">Unidade 1</option>
                            <option value="U2">Unidade 2</option>
                            <option value="U3">Unidade 3</option>

                        </select> <br />
                        <button>Mandar Nota</button>
                    </form> <br />
                    <h2>Buscar Notas do aluno</h2>
                    <form onSubmit={verNotasAluno}>
                        <span>Nome:</span> <input type={'text'} onChange={(e) => { setLupaAluno(e.target.value) }} required /> <br />
                        <br />
                        <button>Buscar Nota</button>
                    </form>
                    <h3>{notasResponse.length !== 0 ? <>{ } </> : <></>}</h3>


                    <button className="logout" onClick={logout}>Fazer Logout</button>
                </div> */}


                </div>


            </div>
            <div className="blackscreenopacity none">
                <div className="floating-box-notas">
                    <div className="floatingboxc">
                        <h3 className="h3login">Insira a nota de <span className="specialspan">{alunoclicadom}</span></h3>
                        <form className="noab" onSubmit={Adcnota}>
                            <span className="spanindicador">Insira uma nota de 0 a 10</span>
                            <input type={'number'} placeholder='Nota' className='inputemail inputemails' step='0.01' required onChange={(e) => { setNota(e.target.value) }} />
                            <span className="spanindicador">Selecione a unidade</span>
                            <select name="unidades" className="selectunidade inputemail" onChange={(e) => { setUnidade(e.target.value) }} required>
                                <option value="U1">Unidade 1</option>
                                <option value="U2">Unidade 2</option>
                                <option value="U3">Unidade 3</option>

                            </select> <br />
                            <button className="submitbutton">Adicionar/Modificar Nota</button>
                        </form>
                        <div className="xsvgdiv" onClick={fechar}>
                            <img src={xsvg}/>
                        </div>
                    </div>
                </div>
            </div>

        </>

    )
}





export default AdmProf
