import axios from "axios";
class Login extends Component {
    constructor(props) {
        super(props)
        this.state = {
            email: '',
            senha: '',
            response: ''
        }
        this.submit = this.submit.bind(this)
    }
    submit() {
        

    }
    render() {
        return (
            <div>
                <center>
                    <h1>Faça login para continuar</h1> <br />
                    <form>
                        <h3>Email:</h3> <br />
                        <input type={'email'} onChange={(e) => { this.setState({ email: e.target.value }) }} /> <br />
                        <h3>Senha:</h3>
                        <input type={'password'} maxLength='11' onChange={(e) => { this.setState({ senha: e.target.value }) }} /> <br /> <br />
                        <button onClick={this.submit}>Entrar</button>
                    </form>
                    <p>{this.state.response === 'EMAIL OU SENHA INVALIDOS' ? <>{this.state.response}</> : <></>}</p>
                    <p>{this.state.response}</p>
                </center>

            </div>
        )
    }
}

export default Login
