import axios from "axios";
import React, { useState } from "react";

const ProfAdmin = () => {

const [nome , setNome] = useState('')
const [email , setEmail] = useState('')
const [sexo , setSexo] = useState('')
const [senha , setSenha] = useState('')
const [materia , setMateria] = useState('')

function submit(e){
    e.preventDefault()
    axios.post('http://localhost:3001/adminRegistro', {nome, email, sexo, senha, materia}).then((response)=>{
    })
    alert('Tudo certo')


}

    return (
        <div>
            <center>
                <h1>Bem vindo! administrador</h1>
                <p>Faça o registro ou login</p>
                <form onSubmit={submit}>
                    <h3>Nome</h3> <br />
                    <input type={'text'} onChange={(e)=>{setNome(e.target.value)}} required /> <br />
                    <h3>Email</h3> <br />
                    <input type={'email'} onChange={(e)=>{setEmail(e.target.value)}} required /><br />
                    <h3>Sexo</h3> <br />
                    <select name="sexo" onChange={(e) => {setSexo(e.target.value)}} required>
                        <option value="M">Masculino</option>
                        <option value="F">Feminino</option>
                    </select> <br />
                    <select name="sexo" onChange={(e) => {setSexo(e.target.value)}} required>
                        <option value="Portugues">Portugues</option>
                        <option value="Matematica">Matematica</option>
                        <option value="Fisica">Fisica</option>
                        <option value="Historia">Historia</option>
                        <option value="Geografia">Geografia</option>

                    </select> <br />
                    <h3>Senha</h3><br />
                    <input type={'password'} onChange={(e)=>{setSenha(e.target.value)}}required /> <br/> 
                    <button>Registrar</button>
                </form>
            </center>
        </div>

    )
}

export default ProfAdmin
