import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
const Portal = () => {
const navigate = useNavigate()
    const [alunoInfo, setAluoinfo] = useState([])
    useEffect(() => {
        const id = localStorage.getItem('IdAluno')
        axios.get(`http://localhost:3001/getAlunoInfo/${id}`).then((response) => {
            if (response.data) {
                setAluoinfo(response.data)
            } else {
                setAluoinfo('Erro')
            }
        })
    })
    function logout(){
        localStorage.removeItem('token')
        localStorage.removeItem('idAluno')
        navigate('/')
    }
    return (

        <>  <center>
            <h2>Olá {alunoInfo.nome}</h2>
            <p>{alunoInfo.portugues === null ? <>Não definido pelo seu professor</> : <>Sua nota em portugues é: <span className="e">{alunoInfo.portugues}</span></> }</p>
            <p>{alunoInfo.matematica === null ? <>Não definido pelo seu professor</> : <> Sua nota em matematica é: <span className="e">{alunoInfo.portugues}</span></> }</p>
            <p>{alunoInfo.historia === null ? <>Não definido pelo seu professor</> : <> Sua nota em historia é: <span className="e">{alunoInfo.portugues}</span></> }</p>
            <p>{alunoInfo.geografia === null ? <>Não definido pelo seu professor</> : <> Sua nota em geografia é: <span className="e">{alunoInfo.portugues}</span></> }</p>
            <p>{alunoInfo.nota_fisica === null ? <>Não definido pelo seu professor</> : <> Sua nota em geografia é: <span className="e">{alunoInfo.nota_fisica}</span></> }</p>

        </center >
        <button className="logout" onClick={logout}>Fazer Logout</button>

        </>



    )

}
export default Portal
