import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
const Portal = () => {
const navigate = useNavigate()
    const [alunoInfo, setAluoinfo] = useState([])
    useEffect(() => {
        const id = localStorage.getItem('IdAluno')
        axios.get(`http://localhost:3001/getAlunoInfo/${id}`).then((response) => {
            if (response.data) {
                setAluoinfo(response.data)
            } else {
                setAluoinfo('Erro')
            }
        })
    })
    function logout(){
        localStorage.removeItem('token')
        localStorage.removeItem('IdAluno')
        navigate('/')
    }
    return (

        <>  <center>
            <h2>Olá {alunoInfo.nome !== '' ? <>{alunoInfo.nome}</>: <>Não definido pelo seu professor</>  }</h2>
            <p>Sua nota em Portugues é {alunoInfo.portugues}</p>
            <p>Sua nota em Matematica é {alunoInfo.matematica}</p>
            <p>Sua nota em Historia é {alunoInfo.historia}</p>
            <p>Sua nota em Geografia é {alunoInfo.geografia}</p>
        </center >
        <button className="logout" onClick={logout}>Fazer Logout</button>

        </>



    )

}
export default Portal
