import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
const Portal = () => {
    const navigate = useNavigate()
    const [alunoInfo, setAluoinfo] = useState([])
    const [numero, setNumero] = useState(5)
    useEffect(() => {
        const id = localStorage.getItem('IdAluno')
        axios.get(`http://localhost:3001/getAlunoInfo/${id}`).then((response) => {
            if (response.data) {
                setAluoinfo(response.data)
            } else {
                setAluoinfo('Erro')
            }
        })
        cor()
    })
    function cor() {
        const span1 = document.querySelector('.e1')
        const span2 = document.querySelector('.e2')
        const span3 = document.querySelector('.e3')
        const span4 = document.querySelector('.e4')
        const span5 = document.querySelector('.e5')
        const span6 = document.querySelector('.e6')
        const span7 = document.querySelector('.e7')
        const span8 = document.querySelector('.e8')
        const span9 = document.querySelector('.e9')
        const span10 = document.querySelector('.e10')
        const span11 = document.querySelector('.e11')
        const span12 = document.querySelector('.e12')

        try {
            if (alunoInfo.portugues < 5) {
                span1.classList.add('ruim')
            } else {
                span1.classList.add('bom')
            }
        } catch (error) {
            return
        }


        try {
            if (alunoInfo.matematica < 5) {
                span2.classList.add('ruim')
            } else {
                span2.classList.add('bom')
            }
        } catch (error) {
            return
        }

        try {
            if (alunoInfo.historia < 5) {
                span3.classList.add('ruim')
            } else {
                span3.classList.add('bom')
            }
        } catch (error) {
            return
        }

        try {
            if (alunoInfo.geografia < 5) {
                span4.classList.add('ruim')
            } else {
                span4.classList.add('bom')
            }
        } catch (error) {
            return
        }

        try {
            if (alunoInfo.nota_fisica < 5) {
                span5.classList.add('ruim')
            } else {
                span5.classList.add('bom')
            }
        } catch (error) {
            return
        }

        try {
            if (alunoInfo.biologia < 5) {
                span6.classList.add('ruim')
            } else {
                span6.classList.add('bom')
            }
        } catch (error) {
            return
        }

        try {
            if (alunoInfo.quimica < 5) {
                span7.classList.add('ruim')
            } else {
                span7.classList.add('bom')
            }
        } catch (error) {
            return
        }

        try {
            if (alunoInfo.redes < 5) {
                span8.classList.add('ruim')
            } else {
                span8.classList.add('bom')
            }
        } catch (error) {
            return
        }

        try {
            if (alunoInfo.imc < 5) {
                span9.classList.add('ruim')
            } else {
                span9.classList.add('bom')
            }
        } catch (error) {
            return
        }

        try {
            if (alunoInfo.ltp < 5) {
                span10.classList.add('ruim')
            } else {
                span10.classList.add('bom')
            }
        } catch (error) {
            return
        }

        try {
            if (alunoInfo.Aps < 5) {
                span11.classList.add('ruim')
            } else {
                span11.classList.add('bom')
            }
        } catch (error) {
            return
        }

        try {
            if (alunoInfo.seg < 5) {
                span12.classList.add('ruim')
            } else {
                span12.classList.add('bom')
            }
        } catch (error) {
            return
        }

    }
    function logout() {
        localStorage.removeItem('token')
        localStorage.removeItem('idAluno')
        navigate('/')
    }
    return (

        <>  <center>
            <h2>Olá {alunoInfo.nome}</h2>
            <span><strong>Abaixo estara as notas da primeira unidade</strong></span>
            <p>{alunoInfo.portugues === null ? <>Não definido pelo seu professor</> : <>Sua nota em Portugues é: <span className="e1">{alunoInfo.portugues}</span></>}</p>
            <p>{alunoInfo.matematica === null ? <>Não definido pelo seu professor</> : <> Sua nota em Matematica é: <span className="e2">{alunoInfo.matematica}</span></>}</p>
            <p>{alunoInfo.historia === null ? <>Não definido pelo seu professor</> : <> Sua nota em Historia é: <span className="e3">{alunoInfo.historia}</span></>}</p>
            <p>{alunoInfo.geografia === null ? <>Não definido pelo seu professor</> : <> Sua nota em Geografia é: <span className="e4">{alunoInfo.geografia}</span></>}</p>
            <p>{alunoInfo.nota_fisica === null ? <>Não definido pelo seu professor</> : <> Sua nota em Fisica é: <span className='e5'>{alunoInfo.nota_fisica}</span></>}</p>
            <p>{alunoInfo.biologia === null ? <>Não definido pelo seu professor</> : <> Sua nota em BIologia é: <span className='e6'>{alunoInfo.biologia}</span></>}</p>
            <p>{alunoInfo.quimica === null ? <>Não definido pelo seu professor</> : <> Sua nota em quimica é: <span className='e7'>{alunoInfo.quimica}</span></>}</p>
            <p>{alunoInfo.redes === null ? <>Não definido pelo seu professor</> : <> Sua nota em Redes é: <span className='e8'>{alunoInfo.redes}</span></>}</p>
            <p>{alunoInfo.imc === null ? <>Não definido pelo seu professor</> : <> Sua nota em IMC é: <span className='e9'>{alunoInfo.imc}</span></>}</p>
            <p>{alunoInfo.ltp === null ? <>Não definido pelo seu professor</> : <> Sua nota em LTP é: <span className='e10'>{alunoInfo.ltp}</span></>}</p>
            <p>{alunoInfo.Aps === null ? <>Não definido pelo seu professor</> : <> Sua nota em APS é: <span className='e11'>{alunoInfo.Aps}</span></>}</p>
            <p>{alunoInfo.seg === null ? <>Não definido pelo seu professor</> : <> Sua nota em Segurança de sistema é: <span className='e12'>{alunoInfo.seg}</span></>}</p>

        </center >
            <button className="logout" onClick={logout}>Fazer Logout</button>

        </>



    )

}
export default Portal
