import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
const AdminLogin = () => {
    const navigate = useNavigate()
    const [email, setEmail] = useState('')
    const [senha, setSenha] = useState('')

    function submit(e) {
        e.preventDefault()
        axios.post('http://localhost:3001/loginAdmin', { email, senha }).then((response) => {
            if (response.data === 'EMAIL OU SENHA INVALIDOS') {
                alert('Email ou senha invalidos')
            } else {
                localStorage.setItem('tokenadminon', true)
                localStorage.setItem('ProfessorID', response.data)
                navigate('/professorMenu')
            }
        })

    }

    return (
        <div className="loginmain">
            <header className="headerlogin">
                <div className="headerloginc">
                    <span className="logo-span-header">SIGEN</span>
                </div>
            </header>
            <h3>Bem vindo! Professor</h3>
            <p>Faça o Login</p>
            <form onSubmit={submit}>
                <h3>Email</h3> <br />
                <input type={'text'} onChange={(e) => { setEmail(e.target.value) }} required /> <br />
                <h3>Senha</h3><br />
                <input type={'password'} onChange={(e) => { setSenha(e.target.value) }} required /> <br />
                <button>Registrar</button>
            </form>

        </div>

    )
}

export default AdminLogin
