import React, { Component } from "react";
import axios from 'axios'
import { Link, useNavigate } from "react-router-dom";

class Registro extends Component {
    constructor(props) {
        super(props)
        this.state = {
            nome: '',
            email: '',
            senha: '',
            telefone: '',
            sexo: 'M',
            turma: 'A'


        }
        this.submit = this.submit.bind(this)
    }
    submit() {
        axios.post('http://localhost:3001/registro', {
            nome: this.state.nome,
            email: this.state.email, senha: this.state.senha, telefone: this.state.telefone, sexo: this.state.sexo, turma: this.state.turma
        }).then(() => {
            alert('Registro feito com sucesso!')
        })
    }

    render() {
        return (
            <div className="loginmain">
                <header className="headerlogin">
                    <div className="headerloginc">
                        <span className="logo-span-header">SIGEN</span>
                    </div>
                </header>
                <div className="maincontainerl">
                    <main className="mainlogin">
                        <h3 className="h3login reg">Faça registro para continuar</h3> <br />
                        <form>
                            <span className="spanre"> <Link to='*'>É professor? Registre-se aqui.</Link> </span>
                            <input type={'text'} className='inputemail' placeholder="Nome" onChange={(e) => { this.setState({ nome: e.target.value }) }} />
                            <input type={'email'} className='inputemail' placeholder="Email" onChange={(e) => { this.setState({ email: e.target.value }) }} />
                            <input type={'password'} className='inputemail' placeholder="Senha" onChange={(e) => { this.setState({ senha: e.target.value }) }} />
                            <input type={'tel'} className='inputemail' placeholder="Telefone" maxLength='11' onChange={(e) => { this.setState({ telefone: e.target.value }) }} />
                            <label to='sexo' className="labelreg">Sexo:</label>
                            <select name="sexo" className='inputemail' onChange={(e) => { this.setState({ sexo: e.target.value }) }}>
                                <option value="M">Masculino</option>
                                <option value="F">Feminino</option>
                            </select>
                            <label to='turma' className="labelreg">Turma:</label>
                            <select name="turma" className='inputemail' onChange={(e) => { this.setState({ turma: e.target.value }) }}>
                                <option value="A">3° INFO A VESPERTINO</option>
                                <option value="B">3° INFO B VESPERTINO</option>
                            </select>

                            <button onClick={this.submit} className='submitbutton'>Cadastrar</button>
                        </form>
                    </main>



                </div>

            </div>
        )
    }
}

export default Registro
