import React, { useState } from "react";
import axios from "axios";
import { Navigate } from "react-router-dom";

function Login() {
    const [email, setEmail] = useState('')
    const [senha, setSenha] = useState('')
    const [response, setResponse] = useState('')

    function submit(e) {
        e.preventDefault()
        axios.post('http://localhost:3001/login', { email: email, senha:senha }).then((response) => {
            if (response.data === 'EMAIL OU SENHA INVALIDOS') {
               setResponse(response.data)
            } else {
              <Navigate replace to='/' />
                console.log('ok')
            }
        })
    }

    return (
        <div>
            <center>
                <h1>Faça login para continuar</h1> <br />
                <form onSubmit={submit}>
                    <h3>Email:</h3> <br />
                    <input type={'email'} onChange={(e) => { setEmail(e.target.value) }} required /> <br />
                    <h3>Senha:</h3>
                    <input type={'password'} maxLength='11' onChange={(e) => { setSenha(e.target.value) }} required /> <br /> <br />
                    <button>Entrar</button>
                </form>
                {response === 'EMAIL OU SENHA INVALIDOS' ? <p>Algo deu errado</p> : <></>}

            </center>
        </div>
    )

}

export default Login
