import axios from "axios";
import React, { useEffect, useState } from "react";


const AdmProf = () => {

    const [responsaAlunos, setResponseAlunos] = useState([])
    const [responseProfessor, setResponseProfessor] = useState([])
    const [Nota, setNota] = useState('')


    useEffect(() => {
        axios.get('http://localhost:3001/getInfoAlunos').then((response) => {
            setResponseAlunos(response.data)
        })
        const id = localStorage.getItem('ProfessorID')
        axios.get(`http://localhost:3001/getInfoProfessor/${id}`).then((response) => {
            setResponseProfessor(response.data)
        })

    }, [])

    function addNotas() {
        const button = document.querySelector('.btn-edt')
        const id_estudante = button.id
        const materia = responseProfessor[1]
        const Nota = Number(prompt('Digite uma nota'))
        setNota(Nota)
        alert(materia)
        axios.post(`http://localhost:3001/addNotas/${id_estudante}, ${materia}`, {Nota}).then(() => {

        })
    }

    return (
        <div>
            <center>
                <h1>Olá Administrador/Professor {responseProfessor[0]} </h1> <br />
                <h3>Abaixo, estara uma lista com todos os alunos do terceiro ano de informatica matriculados no BD.</h3>
                <h3>Sua materia é: {responseProfessor[1]}</h3>
                <div>

                    <table>
                        <tr>
                            <th>Nome</th>
                            <th>Telefone</th>
                            <th>Sexo</th>
                            <th>Turma</th>
                        </tr>
                        <tr>
                            <td>{responsaAlunos.map((e) => {
                                return (
                                    <tr key={e.id}>{e.nome}</tr>
                                )
                            })}</td>
                            <td>{responsaAlunos.map((e) => {
                                return (
                                    <tr key={e.id}>{e.telefone}</tr>
                                )
                            })}</td>
                            <td>
                                {responsaAlunos.map((e) => {
                                    return (
                                        <tr key={e.id}>{e.sexo}</tr>
                                    )
                                })}
                            </td>
                            <td>
                                {responsaAlunos.map((e) => {
                                    return (
                                        <tr key={e.id}>{e.turma}</tr>
                                    )
                                })}
                            </td>
                            <td>
                                {responsaAlunos.map((e) => {
                                    return (
                                        <tr key={e.id}><button onClick={addNotas} id={e.id} className='btn-edt'>Editar notas</button> </tr>
                                    )
                                })}
                            </td>
                        </tr>

                    </table>


                </div>
            </center>
        </div>
    )
}





export default AdmProf
