                        <p>Lixo</p>
                        <p>Lixo</p>

                        <table class="GeneratedTable">
                            <thead>
                                <tr>
                                    <th>Nome</th>
                                    <th>Sexo</th>
                                    <th>Telefone</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    {responsaAlunos.map((e)=>{
                                        return(
                                            <td key={e.id}>{e.nome}</td>
                                            
                                        )
                                    })}
                                </tr>
                                <tr>
                                {responsaAlunos.map((e)=>{
                                        return(
                                            <td key={e.id}>{e.nome}</td>
