import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
const Portal = () => {
    const navigate = useNavigate()
    const [alunoInfo, setAluoinfo] = useState([])
    useEffect(() => {
        const id = localStorage.getItem('IdAluno')
        axios.get(`http://localhost:3001/getAlunoInfo/${id}`).then((response) => {
            if (response.data) {
                setAluoinfo(response.data)
            } else {
                setAluoinfo('Erro')
            }
        })
        cor()
    })
    function cor() {
        const span = document.querySelector('.e')
        if (Number(alunoInfo.portugues) < 5) {
            span.classList.add('ruim')
        } else {
            span.classList.add('bom')
        }
        if (alunoInfo.matematica < 5) {
            span.classList.add('ruim')
        } else {
            span.classList.add('bom')
        }
        if (Number(alunoInfo.nota_fisica) < 5) {
            span.classList.add('ruim')
        } else {
            span.classList.add('bom')
        }
        if (alunoInfo.historia < 5) {
            span.classList.add('ruim')
        } else {
            span.classList.add('bom')
        }
        if (alunoInfo.geografia < 5) {
            span.classList.add('ruim')
        } else {
            span.classList.add('bom')
        }
        if (alunoInfo.geografia < 5) {
            span.classList.add('ruim')
        } else {
            span.classList.add('bom')
        }
    }
    function logout() {
        localStorage.removeItem('token')
        localStorage.removeItem('idAluno')
        navigate('/')
    }
    return (

        <>  <center>
            <h2>Olá {alunoInfo.nome}</h2>
            <p>{alunoInfo.portugues === null ? <>Não definido pelo seu professor</> : <>Sua nota em Portugues é: <span className="e">{alunoInfo.portugues}</span></>}</p>
            <p>{alunoInfo.matematica === null ? <>Não definido pelo seu professor</> : <> Sua nota em Matematica é: <span className="e">{alunoInfo.matematica}</span></>}</p>
            <p>{alunoInfo.historia === null ? <>Não definido pelo seu professor</> : <> Sua nota em Historia é: <span className="e">{alunoInfo.historia}</span></>}</p>
            <p>{alunoInfo.geografia === null ? <>Não definido pelo seu professor</> : <> Sua nota em Geografia é: <span className="e">{alunoInfo.geografia}</span></>}</p>
            <p>{alunoInfo.nota_fisica === null ? <>Não definido pelo seu professor</> : <> Sua nota em Fisica é: <span className="e">{alunoInfo.nota_fisica}</span></>}</p>

        </center >
            <button className="logout" onClick={logout}>Fazer Logout</button>

        </>



    )

}
export default Portal
