import axios from "axios";
import React, { useEffect, useState } from "react";


const AdmProf = () => {

    const [responsaAlunos, setResponseAlunos] = useState([])
    const [responseProfessor, setResponseProfessor] = useState('')


    useEffect(() => {
        axios.get('http://localhost:3001/getInfoAlunos').then((response) => {
            setResponseAlunos(response.data)
        })
        const id = localStorage.getItem('ProfessorID')
        axios.get(`http://localhost:3001/getInfoProfessor/${id}`).then((response) => {
            setResponseProfessor(response.data)
        })

    }, [])

    return (
        <div>
            <center>
                <h1>Olá Administrador/Professor {responseProfessor} </h1> <br />
                <h3>Abaixo, estara uma lista com todos os alunos matriculados no BD.</h3>
                <div>

                    <table>
                        <tr>
                            <th>Nome</th>
                            <th>Telefone</th>
                            <th>Sexo</th>
                        </tr>
                        <tr>
                            <td>{responsaAlunos.map((e) => {
                                return (
                                    <tr>{e.nome}</tr>
                                )
                            })}</td>
                            <td>{responsaAlunos.map((e) => {
                                return (
                                    <tr>{e.telefone}</tr>
                                )
                            })}</td>
                            <td>
                                {responsaAlunos.map((e) => {
                                    return (
                                        <tr>{e.sexo}</tr>
                                    )
                                })}
                            </td>
                        </tr>

                    </table>


                </div>
            </center>
        </div>
    )
}





export default AdmProf
