import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const LoginT = () => {
    const [password, setPassword] = useState('')
    const navigate = useNavigate()

    function submit(e) {
        e.preventDefault()

        axios.post('http://localhost:3001/adminpass', { password }).then((response) => {
            if (response.data === 'EMAIL OU SENHA INVALIDOS') {
                navigate('/')
            } else {
                alert('ok')
                localStorage.setItem('tokenadmin', true)
                navigate('/registroprof')
            }
        })

    }
    return (
        <div className="loginmain">
            <header className="headerlogin">
                <div className="headerloginc">
                    <span className="logo-span-header">SIGEN</span>
                </div>
            </header>
            <div className="maincontainerl">
                <main className="mainlogin">
                    <h1>Digite primeiramente a senha de administrador para continuar</h1> <br />

                    <form onSubmit={submit}>
                        <input type={'password'} onChange={(e) => { setPassword(e.target.value) }} />
                        <button>Login</button>
                    </form>
                </main>
            </div>



        </div>
    )
}





export default LoginT
