import axios from "axios";
import React, { useState } from "react";

const ProfAdmin = () => {
    const [nome, setNome] = useState('')
    const [email, setEmail] = useState('')
    const [sexo, setSexo] = useState('M')
    const [senha, setSenha] = useState('')
    const [materia, setMateria] = useState('Portugues')

    function submit(e) {
        e.preventDefault()
        axios.post('http://localhost:3001/adminRegistro', { nome, email, sexo, senha, materia }).then((response) => {
        })
        localStorage.setItem('tokenadminon')
    }

    return (
        <div className="loginmain">
            <header className="headerlogin">
                <div className="headerloginc">
                    <span className="logo-span-header">SIGEN</span>
                </div>
            </header>
            <div className="maincontainerl">
                <main className="mainlogin">
                    <h3 className="h3login regp">Bem vindo! administrador, Faça seu registro agora</h3>
                    <form onSubmit={submit} className='profreg'>
                        <input type={'text'} placeholder='Nome' className='inputemail' onChange={(e) => { setNome(e.target.value) }} required /> <br />
                        <input type={'email'} placeholder='Email' className='inputemail' onChange={(e) => { setEmail(e.target.value) }} required /><br />
                        <label to='sexo' className="labelreg">Sexo:</label>
                        <select name="sexo" onChange={(e) => { setSexo(e.target.value) }} className='inputemail' required >
                            <option value="M">Masculino</option>
                            <option value="F">Feminino</option>
                        </select> <br />
                        <label to='materias' className="labelreg">Materia:</label>
                        <select name="materias" className='inputemail' onChange={(e) => { setMateria(e.target.value) }} required>
                            <option value="Portugues">Portugues</option>
                            <option value="Matematica">Matematica</option>
                            <option value="Fisica">Fisica</option>
                            <option value="Historia">Historia</option>
                            <option value="Geografia">Geografia</option>

                        </select> <br />
                        <input type={'password'} placeholder='Senha' className='inputemail' onChange={(e) => { setSenha(e.target.value) }} required /> <br />
                        <button className="submitbutton">Registrar</button>
                    </form>
                </main>
            </div>


        </div>

    )
}

export default ProfAdmin
