import React, { Component } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
class Login extends Component {
    constructor(props) {
        super(props)
        this.state = {
            email: '',
            senha: '',
            responsey: ''
        }
        this.submit = this.submit.bind(this)
    }
    submit(e) {
        e.preventDefault()
        axios.post('http://localhost:3001/login', { email: this.state.email, senha: this.state.senha }).then((response) => {
            if (response.data) {
                this.setState({ responsey: response.data })
            } else {
                const Navigate = useNavigate()
                Navigate('/')
            }
        })

    }
    render() {
        return (
            <div>
                <center>
                    <h1>Faça login para continuar</h1> <br />
                    <form onSubmit={this.submit}>
                        <h3>Email:</h3> <br />
                        <input type={'email'} onChange={(e) => { this.setState({ email: e.target.value }) }} required /> <br />
                        <h3>Senha:</h3>
                        <input type={'password'} maxLength='11' onChange={(e) => { this.setState({ senha: e.target.value }) }} required /> <br /> <br />
                        <button>Entrar</button>
                    </form>
                    {this.state.responsey === 'EMAIL OU SENHA INVALIDOS' ? <p>Algo deu errado</p> : <></>}
          
                </center>

            </div>
        )
    }
}

export default Login
