import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
const Portal = () => {
    const navigate = useNavigate()
    const [alunoInfo, setAluoinfo] = useState([])
    const [numero, setNumero] = useState(5)
    useEffect(() => {
        const id = localStorage.getItem('IdAluno')
        axios.get(`http://localhost:3001/getAlunoInfo/${id}`).then((response) => {
            if (response.data) {
                setAluoinfo(response.data)
            } else {
                setAluoinfo('Erro')
            }
        })
        cor()
    })
    function cor() {
        const span1 = document.querySelector('.e1')
        const span2 = document.querySelector('.e2')
        const span3 = document.querySelector('.e3')
        const span4 = document.querySelector('.e4')
        const span5 = document.querySelector('.e5')
        try {
            if (alunoInfo.portugues < 5) {
                span1.classList.add('ruim')
            } else {
                span1.classList.add('bom')
            }
        } catch (error) {
            console.log('nada')
        }


        try {
            if (alunoInfo.matematica < 5) {
                span2.classList.add('ruim')
            } else {
                span2.classList.add('bom')
            }
        } catch (error) {
            console.log('nada')
        }

        try {
            if (alunoInfo.historia < 5) {
                span3.classList.add('ruim')
            } else {
                span3.classList.add('bom')
            }
        } catch (error) {
            console.log('nada')
        }

        try {
            if (alunoInfo.geografia < 5) {
                span4.classList.add('ruim')
            } else {
                span4.classList.add('bom')
            }
        } catch (error) {
            console.log('nada')
        }
        try {
            if (alunoInfo.nota_fisica < 5) {
                span5.classList.add('ruim')
            } else {
                span5.classList.add('bom')
            }
        } catch (error) {
            console.log('nada')
        }

    }
    function logout() {
        localStorage.removeItem('token')
        localStorage.removeItem('idAluno')
        navigate('/')
    }
    return (

        <>  <center>
            <h2>Olá {alunoInfo.nome}</h2>
            <p>{alunoInfo.portugues === null ? <>Não definido pelo seu professor</> : <>Sua nota em Portugues é: <span className="e1">{alunoInfo.portugues}</span></>}</p>
            <p>{alunoInfo.matematica === null ? <>Não definido pelo seu professor</> : <> Sua nota em Matematica é: <span className="e2">{alunoInfo.matematica}</span></>}</p>
            <p>{alunoInfo.historia === null ? <>Não definido pelo seu professor</> : <> Sua nota em Historia é: <span className="e3">{alunoInfo.historia}</span></>}</p>
            <p>{alunoInfo.geografia === null ? <>Não definido pelo seu professor</> : <> Sua nota em Geografia é: <span className="e4">{alunoInfo.geografia}</span></>}</p>
            <p>{alunoInfo.nota_fisica === null ? <>Não definido pelo seu professor</> : <> Sua nota em Fisica é: <span className='e5'>{alunoInfo.nota_fisica}</span></>}</p>

        </center >
            <button className="logout" onClick={logout}>Fazer Logout</button>

        </>



    )

}
export default Portal
