import React, { Component } from "react";
import axios from 'axios'

class Registro extends Component {
    constructor(props) {
        super(props)
        this.state = {
            nome: '',
            email: '',
            senha: '',
            telefone: '',
            sexo:'M',
            turma:'A'


        }
        this.submit = this.submit.bind(this)
    }
    submit() {
        axios.post('http://localhost:3001/registro', {
            nome: this.state.nome,
            email: this.state.email, senha: this.state.senha, telefone: this.state.telefone, sexo:this.state.sexo, turma:this.state.turma
        }).then(() => {
            alert('Registro feito com sucesso!')
        })
    }

    render() {
        return (
            <div className="loginmain">
            
                    <h1>Faça registro para continuar</h1> <br />
                    <h3>Nome completo:</h3> <br />
                    <form>
                        <input type={'text'} onChange={(e) => { this.setState({ nome: e.target.value }) }} /><br />
                        <h3>Email:</h3> <br />
                        <input type={'email'} onChange={(e) => { this.setState({ email: e.target.value }) }} /> <br />
                        <h3>Senha:</h3> <br />
                        <input type={'password'} onChange={(e) => { this.setState({ senha: e.target.value }) }} /> <br />
                        <h3>Telefone:</h3>
                        <input type={'tel'} maxLength='11' onChange={(e) => { this.setState({ telefone: e.target.value }) }} /> <br /> <br />
                        <h3>Sexo:</h3>
                        <select name="sexo" onChange={(e)=>{this.setState({sexo:e.target.value})}}>
                            <option value="M">Masculino</option>
                            <option value="F">Feminino</option>
                        </select>
                        <h3>Turma:</h3>
                        <select name="turma" onChange={(e)=>{this.setState({turma:e.target.value})}}>
                            <option value="A">3° INFO A VESPERTINO</option>
                            <option value="B">3° INFO B VESPERTINO</option>
                        </select>
                        <br/>
                        <br/>
                        <button onClick={this.submit}>Cadastrar</button>
                    </form>

          
            </div>
        )
    }
}

export default Registro
