import axios from "axios";
import React, { useEffect, useState } from "react";
import './style.css'
const AdmProf = () => {
    const [responsaAlunos, setResponseAlunos] = useState([])
    const [responseProfessor, setResponseProfessor] = useState([])
    const [Notas, setNota] = useState('')
    const [alunoBuscado, setAlunoB] = useState('')
    const [resposta, SetResposta] = useState('')
    const [id_estudante, setID] = useState('')
    const [materia, setMateria] = useState('')

    useEffect(() => {
        axios.get('http://localhost:3001/getInfoAlunos').then((response) => {
            setResponseAlunos(response.data)
        })
        const id = localStorage.getItem('ProfessorID')
        axios.get(`http://localhost:3001/getInfoProfessor/${id}`).then((response) => {
            setResponseProfessor(response.data)
            setMateria(responseProfessor[1])
        })

    }, [])
    function buscar(e) {
        e.preventDefault()
        axios.post('http://localhost:3001/buscarAlunos', { alunoBuscado }).then((response) => {
            if (response.data === 'ALUNO NÃO ENCONTRADO') {
                SetResposta(response.data)
                alert(resposta)
            } else {
                const dive = document.querySelector('.divmudar')
                dive.style.display = 'block'
                setID(response.data)
                alert(id_estudante)

            }

        })
    }
    function Adcnota(e) {
        e.preventDefault()
        axios.post(`http://localhost:3001/addNotas/${[id_estudante, materia]}`, { Notas }).then((response) => {
            if (response.data) {
               alert('DEU BOM')
            } else {
                alert('DEU RUIM')

    
            }
        })
    }
 

    return (
        <div>
            <h2>Digite o nome do aluno que você quer editar as notas</h2><br />
            <form>
            <span>Nome:</span> <input type={'search'} onChange={(e) => { setAlunoB(e.target.value) }} required /> <br/>
                <span>Nota:</span>
                <input type={'number'} required onChange={(e)=>{setNota(e.target.value)}} /> 
                    <button>Mandar Nota</button>
            </form>
            <center>
                <h1>Olá Administrador/Professor {responseProfessor[0]} </h1> <br />
                <h3>Abaixo, estara uma lista com todos os alunos do terceiro ano de informatica matriculados no BD.</h3>
                <h3>Sua materia é: {responseProfessor[1]}</h3>

                {responsaAlunos.map((e) => {
                    return (
                        <p key={e.id}>Nome: {e.nome} | Telefone: {e.telefone} | Sexo: {e.sexo} | Turma: {e.turma} </p>
                    )
                })}

            </center>
            <div className="divmudar" style={{ display: 'none' }}>
                <h2>Digite aqui a nota do aluno em {responseProfessor[1]}</h2>
                <form onSubmit={Adcnota}>
                   
                </form>
            </div>
        </div>

    )
}





export default AdmProf
