import axios from "axios";
import React, { useEffect, useState } from "react";


const AdmProf = () => {

    const [responsaAlunos, setResponseAlunos] = useState('')
    const [responseProfessor, setResponseProfessor] = useState('')


    useEffect(() => {
        axios.get('http://localhost:3001/getInfoAlunos').then((response) => {
            setResponseAlunos(response.data)
        })
        axios.get(`http://localhost:3001/getInfoProfessor/${localStorage.getItem('ProfessorID')}`.then((response) => {
            setResponseProfessor(response.data)
        }))
    }, [])

    return (
        <div>
            <center>
                <h1>Olá Administrador/Professor {responseProfessor[0].name}</h1> <br />
                <h3>Abaixo, estara uma lista com todos os alunos matriculados.</h3>
                <span></span>
            </center>
        </div>
    )
}





export default AdmProf
