import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
const AdminLogin = () => {
    const navigate = useNavigate()
    const [email, setEmail] = useState('')
    const [senha, setSenha] = useState('')

    function submit(e) {
        e.preventDefault()
        axios.post('http://localhost:3001/loginAdmin', { email, senha }).then((response) => {
            if (response.data === 'EMAIL OU SENHA INVALIDOS') {
                alert('Email ou senha invalidos')
            } else {
                localStorage.setItem('tokenadminon', true)
                localStorage.setItem('ProfessorInfo', response.data[0])
                navigate('/professorMenu')
            }
        })

    }

    return (
        <div>
            <center>
                <h1>Bem vindo! administrador</h1>
                <p>Faça o Login</p>
                <form onSubmit={submit}>
                    <h3>Email</h3> <br />
                    <input type={'text'} onChange={(e) => { setEmail(e.target.value) }} required /> <br />
                    <h3>Senha</h3><br />
                    <input type={'password'} onChange={(e) => { setSenha(e.target.value) }} required /> <br />
                    <button>Registrar</button>
                </form>
            </center>
        </div>

    )
}

export default AdminLogin
